import turtle

# Создаем объект черепахи
t = turtle.Turtle()

# Рисуем букву "h"
t.penup()
t.goto(-100, 0)  # переместить черепаху в начальную позицию для буквы "h"
t.pendown()
t.left(90)
t.forward(100)
t.right(90)
t.forward(50)
t.backward(100)
t.penup()

# Рисуем букву "i"
t.goto(-50, 0)  # переместить черепаху в начальную позицию для буквы "i"
t.pendown()
t.forward(50)
t.penup()
t.goto(-50, 100)
t.pendown()
t.circle(25, -180)

# Скрыть черепаху и завершить программу
t.hideturtle()
turtle.done()